import React from 'react';
import { TextInput } from '../../components/TextInput';
import SvgIcons from '../../components/svg';
import { SimpleButton } from '../../components/SimpleButton';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

export const SignUp = () => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      Name: '',
      Email: '',
      Password: '',
    },
    validationSchema: Yup.object({
      Name: Yup.string().required('Name is required'),
      Email: Yup.string().email('Invalid email address').required('Email is required'),
      Password: Yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors }) => {
      try {
        const response = await axios.post('https://tmf-backend.onrender.com/users/signup', values);
        console.log('Signup successful:', response.data);
        navigate('/login');
      } catch (error) {
        console.error('Signup failed:', error);
        setErrors({ apiError: error.response?.data?.message || 'Signup failed' });
      } finally {
        setSubmitting(false);
      }
    },
  });
  return (
    <div className='w-full flex justify-center'>
      <div className='w-[90%] md:w-[480px] border-[1px] border-black flex flex-col items-center mt-20'>
        <h2 className='text-[30px] md:text-[38px] font-bold font-poppins py-3'>
          TMF
        </h2>
        <h1 className='text-[40px] md:text-[48px] font-bold font-roboto '>
          Sign Up
        </h1>
        <p className='text-[14px] md:text-[18px]  font-roboto py-3'>
          Signup to Tallmanfilms
        </p>
   <form onSubmit={formik.handleSubmit}>

        <div className='w-[90%] md:w-[80%]'>
          <TextInput
            label='Name*'
            // type='text'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''
            error={formik.errors.Name}
            id="Name"
         name="Name"
         type="text"
         onChange={formik.handleChange}
         value={formik.values.Name}
          />
        </div>
        <div className='w-[90%] md:w-[80%]'>
          <TextInput
            label='Email*'
            // type='text'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''

            id="Email"
            name="Email"
            type="email"
            onChange={formik.handleChange}
            value={formik.values.Email}

          />
        </div>

        <div className='w-[90%] md:w-[80%] pb-5'>
          <TextInput
            label='Password*'
            // type='password'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            error=''
            icon={<SvgIcons.Eye />}

            id="Password"
            name="Password"
            type="password"
            onChange={formik.handleChange}
            value={formik.values.Password}
          />
        </div>
        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            text='Sign Up'
            type='submit'
            style='dark'
            onClick={() => {
              console.log('clicked');
            }}
            disabled={false}
          />
        </div>
   </form>


        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            text={
              <div className='flex gap-5'>
                <SvgIcons.GoogleBlack /> Sign up with Google
              </div>
            }
            style='light'
            onClick={() => {
              console.log('clicked');
            }}
            disabled={true}
          />
        </div>

        <p className='text-[14px] md:text-[16px] flex  font-roboto py-3 pb-5 gap-2'>
          Already have an account?
          <span className='cursor-pointer underline' onClick={()=>{navigate('/login')}}>{` Login`}</span>
        </p>
      </div>
    </div>
  );
};
