import React from 'react';
import { TextInput } from '../../components/TextInput';
import SvgIcons from '../../components/svg';
import { SimpleButton } from '../../components/SimpleButton';
import { useNavigate } from 'react-router-dom';

export const SignUp = () => {
  const navigate = useNavigate();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSignUp = async () => {
    try {
      const response = await fetch('https://tmf-backend.onrender.com/users/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate('/login');
      } else {
        setError(data.message || 'Something went wrong. Please try again.');
      }
    } catch (error) {
      setError('Failed to sign up. Please check your network and try again.');
    }
  };


  return (
    <div className='w-full flex justify-center'>
      <div className='w-[90%] md:w-[480px] border-[1px] border-black flex flex-col items-center mt-20'>
        <h2 className='text-[30px] md:text-[38px] font-bold font-poppins py-3'>
          TMF
        </h2>
        <h1 className='text-[40px] md:text-[48px] font-bold font-roboto '>
          Sign Up
        </h1>
        <p className='text-[14px] md:text-[18px]  font-roboto py-3'>
          Signup to Tallmanfilms
        </p>

        <div className='w-[90%] md:w-[80%]'>
          <TextInput
            // label='Name*'
            // type='text'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''

            label='Name*'
            type='text'
            value={name}
            onChange={(e) => setName(e.target.value)}
            error={error}
          />
        </div>
        <div className='w-[90%] md:w-[80%]'>
          <TextInput
            // label='Email*'
            // type='text'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''

            label='Email*'
            type='text'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            error={error}
          />
        </div>

        <div className='w-[90%] md:w-[80%] pb-5'>
          <TextInput
            // label='Password*'
            // type='password'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''
            // icon={<SvgIcons.Eye />}

            label='Password*'
            type='password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            error={error}
            icon={<SvgIcons.Eye />}
          />
        </div>
        {error && <div className='text-red-500'>{error}</div>}
        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            // text='Sign Up'
            // style='dark'
            // onClick={() => {
            //   console.log('clicked');
            // }}
            // disabled={false}

            text='Sign Up'
            style='dark'
            onClick={handleSignUp}
            disabled={!name || !email || !password}
          />
        </div>
        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            text={
              <div className='flex gap-5'>
                <SvgIcons.GoogleBlack /> Sign up with Google
              </div>
            }
            style='light'
            onClick={() => {
              console.log('clicked');
            }}
            disabled={true}
          />
        </div>

        <p className='text-[14px] md:text-[16px] flex  font-roboto py-3 pb-5 gap-2'>
          Already have an account?
          <span className='cursor-pointer underline' onClick={()=>{navigate('/login')}}>{` Login`}</span>
        </p>
      </div>
    </div>
  );
};
