import React from 'react';
import { TextInput } from '../../components/TextInput';
import SvgIcons from '../../components/svg';
import { SimpleButton } from '../../components/SimpleButton';
import { useNavigate } from 'react-router-dom';
import OTPInput from '../../components/OTPInput';

export const OTP = () => {
  const navigate = useNavigate();
  const [otp, setOtp] = useState(new Array(4).fill(''));
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const handleOTPChange = (valueArray) => {
    setOtp(valueArray);
  };

  const handleConfirmClick = async () => {
    try {
      const otpCode = otp.join('');
      const response = await axios.post('https://tmf-backend.onrender.com/users/reset-password', {
        otp: otpCode,
      });

      if (response.status === 200) {
        setSuccess('OTP verified successfully!');
        navigate('/resetpassword_two'); 
      } else {
        setError('Invalid OTP. Please try again.');
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to verify OTP. Please try again.');
    }
  };
  return (
    <div className='w-full flex justify-center'>
      <div className='w-[90%] md:w-[480px] border-[1px] border-black flex flex-col items-center mt-20'>
        <h2 className='text-[30px] md:text-[38px] font-bold font-poppins py-3'>
          TMF
        </h2>
        <h1 className='text-[40px] md:text-[48px] font-bold font-roboto '>
          Enter OTP
        </h1>
        <p className='text-[14px] md:text-[18px]  font-roboto py-3 w-[90%] md:w-[80%] text-center'>
          Please enter the four digit code you received on your email
        </p>
        {/* <div className='w-[90%] md:w-[80%] py-5'>
          <OTPInput valueArray={new Array(4).fill('')} />
        </div>

        <p className='text-[14px] md:text-[18px]  font-roboto py-3'>
          Resend OTP
        </p> */}


<div className='w-[90%] md:w-[80%] py-5'>
          <OTPInput valueArray={otp} onChange={handleOTPChange} />
        </div>

        {error && <div className='text-red-500'>{error}</div>}
        {success && <div className='text-green-500'>{success}</div>}

        <div className='w-[90%] md:w-[80%] pb-8'>
          <SimpleButton
            text='Confirm'
            style='dark'
            onClick={() => {
              console.log('clicked');
            }}
            disabled={false}
          />
        </div>

        {/* <p className='text-[14px] md:text-[16px] flex  font-roboto py-3 pb-5 gap-2'>
            Don't have an account?
          <span className='cursor-pointer underline' onClick={()=>{navigate('/signup')}}>{` Sign Up`}</span>
        </p> */}
      </div>
    </div>
  );
};
