import React, { useState } from 'react';
import { TextInput } from '../../components/TextInput';
import SvgIcons from '../../components/svg';
import { SimpleButton } from '../../components/SimpleButton';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const Login = () => {
    // const navigate = useNavigate();
    // const [email, setEmail] = useState('');
    // const [password, setPassword] = useState('');
    // const [error, setError] = useState('');




    // const handleLogin = async () => {
    //   try {
    //     const response = await axios.post('https://tmf-backend.onrender.com/users/login', {
    //       email : email,
    //        password : password,
    //     });

    //     if (response.status === 200) {
    //       navigate('/');
    //     } else {
    //       setError('Login failed. Please try again.');
    //     }
    //   } catch (err) {
    //     setError(err.response?.data?.message || 'Login failed. Please try again.');
    //   }
    // };

    let formik = useFormik({
        initialValues: {
          email: '',
          password: '',
          error: '',
          SyntaxError: '',
        },
        validate: (values) => {
          const errors = {};    
          if (!values.email) {
            errors.email = 'Email is required';
          } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
            errors.email = 'Invalid email address';
          }

          if (!values.password) {
            errors.password = 'Password is required';
          } else if (values.password.length < 6) {
            errors.password = 'Password must be at least 6 characters';
          }
          return errors;
        },
        onSubmit: async (values) => {
          try {
            const response = await axios.post('https://tmf-backend.onrender.com/users/login', {
              email : values.email,
              password : values.password,
            });

            if (response.status === 200) {
              navigate('/');
            } else {
              setError('Login failed. Please try again.');
            }
          } catch (err) {
            setError(err.response?.data?.message || 'Login failed. Please try again.');
          }
        },
      });
  return (
    <div className='w-full flex justify-center'>
      <div className='w-[90%] md:w-[480px] border-[1px] border-black flex flex-col items-center mt-20'>
        <h2 className='text-[30px] md:text-[38px] font-bold font-poppins py-3'>
          TMF
        </h2>
        <h1 className='text-[40px] md:text-[48px] font-bold font-roboto '>
          Welcome!
        </h1>
        <p className='text-[14px] md:text-[18px]  font-roboto py-3'>
          Login to Tallmanfilms
        </p>


        <div className='w-[90%] md:w-[80%]'>
          <TextInput
           label='Email*'
           type='text'
           value={email}
           onChange={(e) => setEmail(e.target.value)}
           error={error}
          />
        </div>

        <div className='w-[90%] md:w-[80%] '>
          <TextInput
            label='Password*'
            type='password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            error={error}
            icon={<SvgIcons.Eye />}
          />
        </div>
        {error && <div className='text-red-500'>{error}</div>}

        <p className='text-[14px] md:text-[16px] flex  font-roboto py-2 pb-5 gap-2'>
            Forgot your password?
          <span className='cursor-pointer underline' onClick={()=>{navigate('/resetpassword-one')}}>{` Reset Here!`}</span>
        </p>

        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
              text='Login'
              style='dark'
              onClick={handleLogin}
              disabled={!email || !password}
          />
        </div>
        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            text={
              <div className='flex gap-5'>
                <SvgIcons.GoogleBlack /> Login with Google
              </div>
            }
            style='light'
            onClick={() => {
              console.log('clicked');
            }}
            disabled={true}
          />
        </div>

        <p className='text-[14px] md:text-[16px] flex  font-roboto py-3 pb-5 gap-2'>
            Don't have an account?
          <span className='cursor-pointer underline' onClick={()=>{navigate('/signup')}}>{` Sign Up`}</span>
        </p>
      </div>
    </div>
  );
};
