import React, { useEffect, useState } from 'react';
import { TextInput } from '../../components/TextInput';
import SvgIcons from '../../components/svg';
import { SimpleButton } from '../../components/SimpleButton';
import { useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
export const SignUp = () => {
  const navigate = useNavigate();

  // const [name, setName] = useState('');
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');
  // const [error, setError] = useState('');


  // const handleSignUp = async () => {
  //   try {
  //     const response = await fetch('https://tmf-backend.onrender.com/users/signup', {
  //       method: 'POST',
  //       headers: {
  //         'Content-Type': 'application/json',
  //       },
  //       body: JSON.stringify({ name, email, password }),
  //     });

  //     const data = await response.json();

  //     if (response.ok) {
  //       navigate('/login');
  //       console.log(data);
  //     } else {
  //       setError(data.message || 'Something went wrong. Please try again.');
  //     }
  //   } catch (error) {
  //     setError('Failed to sign up. Please check your network and try again.');
  //   }
  // };

  let formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      error: '',
      SyntaxError: '',
    },
    validate: (values) => {
      const errors = {};

      if (!values.name) {
        errors.name = 'Name is required';
      } else if (values.name.length < 2) {
        errors.name = 'Name must be at least 2 characters';
      }

      if (!values.email) {
        errors.email = 'Email is required';
      } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
        errors.email = 'Invalid email address';
      }

      if (!values.password) {
        errors.password = 'Password is required';
      } else if (values.password.length < 8) {
        errors.password = 'Password must be at least 8 characters';
      } else if (!/(?=.*[0-9])(?=.*[!@#$%^&*])(?=.*[a-zA-Z])/i.test(values.password)) {
        errors.password = 'Password must include at least one letter, one number, and one special character';
      }

      return errors;
    }


  });

  useEffect(() => {
    if (formik.values.error) formik.setValues({ ...formik.values, error: '' });
    if (formik.values.SyntaxError)
      formik.setValues({ ...formik.values, SyntaxError: '' });
  }
  , [formik.values.name, formik.values.email, formik.values.password]);

  const handleSignUp = async () => {
    if (formik.values.error) formik.setValues({ ...formik.values, error: '' });
    if (formik.values.SyntaxError)
      formik.setValues({ ...formik.values, SyntaxError: '' });

    if (!formik.values.name || !formik.values.email || !formik.values.password) {
      formik.setValues({
        ...formik.values,
        error: 'All fields are required',
      });
      return;
    }
    try {
      const response = await fetch('https://tmf-backend.onrender.com/users/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: formik.values.name, email: formik.values.email, password: formik.values.password }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate('/login');
        console.log(data);
      } else {
        formik.setValues({
          ...formik.values,
          error: data.message || 'Something went wrong. Please try again.',
        });
      }
    } catch (error) {
      formik.setValues({
        ...formik.values,
        error: 'Failed to sign up. Please check your network and try again.',
      });
    }
  };








  return (
    <div className='w-full flex justify-center'>
      <div className='w-[90%] md:w-[480px] border-[1px] border-black flex flex-col items-center mt-20'>
        <h2 className='text-[30px] md:text-[38px] font-bold font-poppins py-3'>
          TMF
        </h2>
        <h1 className='text-[40px] md:text-[48px] font-bold font-roboto '>
          Sign Up
        </h1>
        <p className='text-[14px] md:text-[18px]  font-roboto py-3'>
          Signup to Tallmanfilms
        </p>

        <div className='w-[90%] md:w-[80%]'>
          <TextInput
            // label='Name*'
            // type='text'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''

            // label='Name*'
            // type='text'
            // value={name}
            // onChange={(e) => setName(e.target.value)}
            // error={error}


            label='Name*'
            type='text'
            value={formik.values.name}
         onChange={(e) => formik.setFieldValue('name', e.target.value)}
            error={formik.errors.name}


          />
        </div>

        <div className='w-[90%] md:w-[80%]'>
          <TextInput
            // label='Email*'
            // type='text'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''

            // label='Email*'
            // type='text'
            // value={email}
            // onChange={(e) => setEmail(e.target.value)}
            // error={error}

            label='Email*'
            type='text'
            value={formik.values.email}
            onChange={(e) =>
              formik.setValues({ ...formik.values, email: e.target.value })
            }
            error={formik.errors.email}

          />
        </div>



        <div className='w-[90%] md:w-[80%] pb-5'>
          <TextInput
            // label='Password*'
            // type='password'
            // onChange={(e) => {
            //   console.log(e.target.value);
            // }}
            // error=''
            // icon={<SvgIcons.Eye />}

            // label='Password*'
            // type='password'
            // value={password}
            // onChange={(e) => setPassword(e.target.value)}
            // error={error}
            // icon={<SvgIcons.Eye />}

            label='Password*'
            type='password'
            value={formik.values.password}
            onChange={(e) =>
              formik.setValues({ ...formik.values, password: e.target.value })
            }
            error={formik.errors.password}
            icon={<SvgIcons.Eye />}

          />
        </div>
        {formik.values.error && (
          <div className='text-red-500'>{formik.values.error}</div>
        )}

        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            // text='Sign Up'
            // style='dark'
            // onClick={() => {
            //   console.log('clicked');
            // }}
            // disabled={false}

            // text='Sign Up'
            // style='dark'
            // onClick={handleSignUp}
            // disabled={!name || !email || !password}


            text='Sign Up'
            style='dark'
            onClick={handleSignUp}
            disabled={
              !formik.values.name || !formik.values.email || !formik.values.password
            }

          />
        </div>
        <div className='w-[90%] md:w-[80%]'>
          <SimpleButton
            text={
              <div className='flex gap-5'>
                <SvgIcons.GoogleBlack /> Sign up with Google
              </div>
            }
            style='light'
            onClick={() => {
              console.log('clicked');
            }}
            disabled={true}
          />
        </div>

        <p className='text-[14px] md:text-[16px] flex  font-roboto py-3 pb-5 gap-2'>
          Already have an account?
          <span className='cursor-pointer underline' onClick={()=>{navigate('/login')}}>{` Login`}</span>
        </p>
      </div>
    </div>
  );
};
